/** Company Competition Graph API
 * Get Graph /api/comp/surrounding
 */

// const COMPANY_GRAPH_API = '/graph/api/comp/surrounding'

const COMPANY_GRAPH_API = 'http://localhost:8000/api/comp/surrounding'
export async function getCompanyGraph (nodeId, expandLayers, comp, prod, other, unkown) {
  try {
    const response = await fetch(`${COMPANY_GRAPH_API}?node_id=${nodeId}&expand_number_of_layers=${expandLayers}`)
    // const response = await fetch(`${COMPANY_GRAPH_API}`)
    const data = await response.json()
    if (data && data.links && data.nodes) {
      let len = data.links.length
      for (let i = 0; i < len; i++) {
        let flag = false
        if (comp !== true && data.links.category === 'competition') {
          flag = true
        }
        if (prod !== true && data.links.category === 'prodcut') {
          flag = true
        }
        if (other !== true && data.links.category === 'other') {
          flag = true
        }
        if (unkown !== true && data.links.category === 'unkown') {
          flag = true
        }
        if (flag) {
          data.links.splice(i, 1)
          i = i - 1
          len = len - 1
        }
      }
      return data
    } else {
      return null
    }
  } catch (e) {
    console.error(e)
  }
}
